import React,{useState,useEffect} from 'react';
import './App.css';
import axios from "axios";

function App() {

 const [record,setRecord]=useState(null);
 const [data,setData]=useState([]);


  useEffect(() => {

  if(record!=null)
  {

    const fetchData = async () => {
      axios
        .get(`https://swapi.dev//api/people/${record}`)
        .then((item ) => {

          const newItem={
            id:record,
            name:item.data.name
          };

          if(!data)
            setData(newItem);
          else
          setData([...data,newItem]);

        });
    };

    fetchData();
  }

  }, [record]);


  const handleClick=(id)=>{


   data.slice(id-1,1);
   data=data.filter(function(item){return item.id!==id} );
   //console.log(data[id-1]);
  setData([...data]);
   console.log(data);

  }

  return (
    <div className="App">
      <div className="add_record_btn" onClick={()=>{setRecord(record+1)}}>Add Record</div>
      <div className="record">
         <div className="rcd_heading">Records</div>
         <div className="rcd_result">
         {data && data.map((dt)=>(
             <div key={dt.id}className="rcd_result_item">
                <div>{dt.name}</div>
                <div className="delete_record" onClick={()=>{handleClick(dt.id)}}>delete</div>
             </div>))
        }
         </div>
      </div>
    </div>
  );
}

export default App;
